        public class ThreadInfo
        {
            private double _totalTime;
            private double _diffTime;
            public string Name;
            public int Id;
            public double TotalTime
            {
                get => _totalTime;
                set
                {
                    var old = _totalTime;
                    _totalTime = value;
                    _diffTime = value - old;
                }
            }
            public double DiffTime
            {
                get => _diffTime;
            }
            public ThreadPriority Priority;
        }
